INSERT INTO `survey` VALUES (101,'IEEE models Survey.', '8fdd0cef-12b4-4359-8047-d36ff85c4293', 'The goal of this survey is to detect which factors affect the decision of picking new technologies within your company. Thanks for answering the survey!');
INSERT INTO `question` VALUES (101,'How important is to understand the architecture and how the different pieces of your application work?',101),(102,'Please rate the following aspects of your application based on importance they have for your time',101),(103,'When your implementation doesn\'t support the business needs, how fast do you generally need to evolve from development to production?',101),(104,'If you deal with production problems, how important is for you to trace back the problem to the development environment',101),(105,'Please rank the following elements based on their importance for the development and maintainance of your Web applications',101),(106,'How important is for you to define the architecture of your Web application?',101),(107,'Please rank the following aspects based on how much time do you spent on each development iteration',101),(108,'How important is the community and the tutorials around the tools and frameworks you use for development?',101),(109,'Would you pay a license for using a framework or tool?',101);
